package com.smartinvoices.app; import android.os.Bundle; import android.webkit.*; import androidx.appcompat.app.AppCompatActivity; public class MainActivity extends AppCompatActivity{ WebView w; protected void onCreate(Bundle b){ super.onCreate(b); w=new WebView(this); setContentView(w); WebSettings s=w.getSettings(); s.setJavaScriptEnabled(true); s.setDomStorageEnabled(true); w.setWebViewClient(new WebViewClient()); w.loadUrl('file:///android_asset/www/login.html'); } public void onBackPressed(){ if(w!=null && w.canGoBack()) w.goBack(); else super.onBackPressed(); }}